from copy import deepcopy

inp = inp.split("\n")
grid = [[a for a in line] for line in inp]

# for line in grid:
#     print("".join(line))
# print()

ans = 0
while True:
    ans += 1
    if ans % 20 == 0:
        print(ans)
    newgrid = [[None for _ in line] for line in grid]
    seen = [[False for _ in line] for line in grid]
    newnewgrid = [[None for _ in line] for line in grid]
    seenseen = [[False for _ in line] for line in grid]
    for i in range(len(grid)):
        for j in range(len(grid[0])):
            if grid[i][j] == ">" and grid[i][(j+1) % (len(grid[0]))] == ".":
                newgrid[i][j] = "."
                newgrid[i][(j+1) % (len(grid[0]))] = ">"
                seen[i][j] = True
                seen[i][(j+1) % (len(grid[0]))] = True
            else:
                if not seen[i][j]:
                    newgrid[i][j] = grid[i][j]
                    seen[i][j] = True
    # for line in newgrid:
    #     print("".join(line))
    # print()
    for i in range(len(newgrid)):
        for j in range(len(newgrid[0])):
            if newgrid[i][j] == "v" and newgrid[(i+1) % (len(newgrid))][j] == ".":
                newnewgrid[i][j] = "."
                newnewgrid[(i+1) % (len(newgrid))][j] = "v"
                seenseen[i][j] = True
                seenseen[(i+1) % (len(newgrid))][j] = True
            else:
                if not seenseen[i][j]:
                    newnewgrid[i][j] = newgrid[i][j]
                    seenseen[i][j] = True
    notsame = 0
    for i in range(len(grid)):
        for j in range(len(grid[0])):
            notsame += 1 if grid[i][j] != newnewgrid[i][j] else 0
    if notsame == 0:
        break
    else:
        grid = deepcopy(newnewgrid)
        # for line in grid:
        #     print("".join(line))
        # print()
print(ans)